import React from 'react';
import { connect } from 'react-redux';

import {Container, Row, Col} from '../BootstrapComponents/BootstrapComponents';
import Listing from './Listing/Listing';
import * as actionTypes from '../../store/actions/actions';

import './Listings.scss';

const Listings = () => {
    return ( 
        <div className="listings bg-light">
            <Container>
                <Row className="d-none d-md-flex">
                    <Col className="listing-results text-left">
                        <p className="text-muted">0 listings in and around {this.props.srch}</p>
                    </Col>
                </Row>
                <Row>
                    <Listing></Listing>
                </Row>
            </Container>
        </div>
     );
}

const mapStateToProps = (state) => {
    return {
        srch: state.searchLocation
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onSearchLocation: value => dispatch({type: actionTypes.SEARCH_LOCATION, value: value})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Listings);
