import * as actionTypes from '../actions/actions';

const initialState = {
    searchLocation: ''
}

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case actionTypes.SEARCH_LOCATION:
            return {
                ...state,
                searchLocation: action.value
            }

    }
    return state;
}

export default reducer;
